import React from "react";
import characters from "../data";
import Cards from "./Cards";

const SearchBar = (props)=> {
   

   const idBuscada = document.querySelector('#itemId')
   
   
   // const filtrado = () => {
   //    const characterSearch = characters.filter((char) => {
   //    return char.id === idBuscada.value;
   //    });
   //    return (
   //       <Cards characters={characterSearch} />
   //    ) 
   // }

   return (
      <div>
         <input type='search' placeholder="Buscar..." id='itemId' />
         <button onClick= {() => {
      const characterSearch = characters.filter((char) => {
      return char.id === idBuscada.value;
      });
      return (
         <Cards characters={characterSearch} />
      ) 
   }}
         
                
         //  {()=>console.log(idBuscada.value)}
         >Agregar</button>
      </div>
   );
}


export default SearchBar
